import React, { useState } from 'react';
import axios from 'axios';

function LettersSearchPanel() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/letters/search?q=' + encodeURIComponent(query), {
        headers: { Authorization: 'Bearer ' + token }
      });
      setResults(res.data);
    } catch {
      setResults([]);
    }
    setLoading(false);
  };

  return (
    <div>
      <form className="mb-3 d-flex" onSubmit={handleSearch}>
        <input
          className="form-control me-2"
          type="search"
          placeholder="Search by subject, letter number, or sender name..."
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
        <button className="btn btn-primary" type="submit" disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </form>
      {results.length > 0 && (
        <div className="table-responsive">
          <table className="table table-bordered table-sm align-middle">
            <thead className="table-danger">
              <tr>
                <th>Letter No</th>
                <th>Subject</th>
                <th>Sender</th>
                <th>Created At</th>
              </tr>
            </thead>
            <tbody>
              {results.map(l => (
                <tr key={l.id}>
                  <td>{l.letter_no}</td>
                  <td>{l.subject}</td>
                  <td>{l.sender_name}</td>
                  <td>{l.created_at}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      {results.length === 0 && query && !loading && <div>No results found.</div>}
    </div>
  );
}

export default LettersSearchPanel;
