import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import UsersAdminTab from '../components/UsersAdminTab';

const tabNames = [
  'Users',
  'Departments',
  'Divisions',
  'Logs',
  'Stats'
];

function AdminDashboard() {
  const [activeTab, setActiveTab] = useState(0);
  const navigate = useNavigate();
  const role = localStorage.getItem('role');

  useEffect(() => {
    if (role !== 'admin') {
      navigate('/');
    }
  }, [role, navigate]);

  if (role !== 'admin') {
    return <div className="container mt-5 text-center">Access denied. Admins only.</div>;
  }

  return (
    <div className="container mt-4">
      <h3 className="mb-3">Admin Dashboard</h3>
      <ul className="nav nav-tabs">
        {tabNames.map((tab, idx) => (
          <li className="nav-item" key={tab}>
            <button className={`nav-link${activeTab === idx ? ' active' : ''}`} onClick={() => setActiveTab(idx)}>{tab}</button>
          </li>
        ))}
      </ul>
      <div className="tab-content p-3 border border-top-0">
        {activeTab === 0 && <UsersAdminTab />}
        {activeTab === 1 && <div>Departments management coming soon...</div>}
        {activeTab === 2 && <div>Divisions management coming soon...</div>}
        {activeTab === 3 && <div>Logs coming soon...</div>}
        {activeTab === 4 && <div>Stats coming soon...</div>}
      </div>
    </div>
  );
}

export default AdminDashboard;
